        // Security Layer: Show warning function
        function showSecurityWarning() {
            if (typeof Swal !== 'undefined') {
                Swal.fire({
                    title: 'üîí Protected Content',
                    text: 'This content is protected and cannot be copied or inspected.',
                    icon: 'warning',
                    confirmButtonColor: '#f39c12',
                    timer: 3000,
                    timerProgressBar: true
                });
            }
        }

        // Security Layer 1: Disable right-click context menu
        document.addEventListener('contextmenu', function(e) {
            e.preventDefault();
            showSecurityWarning();
            return false;
        });

        // Security Layer 2: Disable text selection (except in input fields)
        document.addEventListener('selectstart', function(e) {
            if (e.target.tagName !== 'INPUT' && e.target.tagName !== 'TEXTAREA' && e.target.tagName !== 'SELECT') {
                e.preventDefault();
                return false;
            }
        });

        // Security Layer 3: Disable drag
        document.addEventListener('dragstart', function(e) {
            e.preventDefault();
            return false;
        });

        // Security Layer 4: Disable keyboard shortcuts
        document.addEventListener('keydown', function(e) {
            // Disable F12 (Developer Tools)
